import React from 'react'
import {connect} from 'react-redux'
import {bindActionCreators} from 'redux'
import HorizontalScrollNav from '../Common/HorizontalScrollNav';
import Filter from './Filter.js';
// eslint-disable-next-line
import LanguageSelect from './LanguageSelect.js';
import Menu from './Menu';

class MenuContainer extends React.Component {
  constructor(props) {
    super(props)

    const paramArray = window.location.href.split('/');
    this.params = {
       requestedVenue: paramArray[3],
       item: paramArray.length === 5 ? paramArray[4] : false,
    }
  }

 componentWillMount() {
   const { getMenuData, bffRes, venue } = this.props;
   if (!bffRes || this.params.requestedVenue !== venue) {
     getMenuData(this.params.requestedVenue);
   }
 }

 componentWillUpdate() {
   const { getMenuData, bffRes, venue } = this.props;
   if (!bffRes || this.params.requestedVenue !== venue) {
     getMenuData(this.params.requestedVenue);
   }
 }

 componentWillUnmount() {
   persistStore(this.props).purge();
 }

 routeToItemDetail(e, id) {
   e.stopPropagation();
   window.location = window.location.href + `/${id}`
 }

  componentDidMount(){
    if (!this.state.update){
      this.setState({update: true})
    }
  }

  getHeader(){
    const {
      venueName,
      sectionPositions,
      showBackArrow,
      showFilter,
      showLanguageSelect,
      filter,
      updateFilter,
      current,
      updateLang,
      lang,
    } = this.props;

    document.title = venueName + " Menu";

    return (
      <div>
        <header className="header">
          {/* back arrow for routing, control this and venuename via props */}
          { showBackArrow ? <img src="/icons/arrow-left-solid-white.svg" className="headerBackArrow" alt="back arrow"/> : null }
          { !!venueName ? <h1 className="venue">{venueName}</h1> : null }
          { showFilter && <Filter filter={filter} updateFilter={updateFilter} /> }
            { Object.keys(sectionPositions).length === 0 ? <span>loading</span> : <HorizontalScrollNav sectionPositions={sectionPositions}/>}
          { showLanguageSelect && <LanguageSelect lang={lang} updateLang={updateLang} /> }
          {/* <img className="cartIcon" src="/icons/cart_icon.svg" alt="cart"/> */}
          {/* need check to see when to display cart badge */}
          {/* { hasCartItems && <div className="cartBadge"/> } */}
        </header>
      </div>
    );
  }

  render() {
    // eslint-disable-next-line
    const {
      venueName,
      sectionPositions,
      showBackArrow,
      showFilter,
      showLanguageSelect,
      filter,
      updateFilter,
      current,
      updateLang,
      lang,
    } = this.props;

    document.title = venueName + " Menu";
    const itemId = this.params.item;

    return (
      isLoading ? <LoadingScreen/> : 
      (
        <div className="Menu">
          {this.getHeader()}
          <div className="menu">
            <Menu
              menuItemKeys={Object.keys(bffRes)}
              menuItems={bffRes}
              filter={filter}
              lang={lang}
              itemId={itemId}
              routeToItemDetail={routeToItemDetail}
            />
            <Water/>
            <Footer/>
          </div>
        </div>
      ) 
    );
  }

};

const mapStateToProps = state => ({
  bffRes: state.persistentMenu.bffRes,
  isLoading: state.persistentMenu.isLoading,
  venue: state.persistentMenu.venue,
  sectionPositions: state.menu.sectionPositions,
  filter: state.persistentMenu.filter,
  lang: state.persistentMenu.lang,
});

export default connect(mapStateToProps)(Header)
