import React from 'react';
import ReactDOM from 'react-dom';
import _ from 'lodash'
import uuid from 'uuid/v4';

const NavBadge = (props) =>  {
    // eslint-disable-next-line
    const { section, onSelect, currentPosition, interval } = props;
    console.log(interval, currentPosition)
    const className = currentPosition >= interval[0] && currentPosition < interval[1] ? 'red' : '';
    return (
        <span
            style={{cursor: 'pointer'}}
            onClick={(e) => {onSelect(e)}}
            id={`$scroll-nav-${section}`}
            key={uuid()}
        >
            {section}
        </span>
    );
}

export default NavBadge;
