export const getSubtotals = item => {
    const itemClone = item;
    let subtotal = 0;
    item.addOns.map(addOn => {
        subtotal = subtotal + addOn.price;
    });

    itemClone.subtotal = (item.price + subtotal) * item.quantity;
    console.log(item.clone)
    return itemClone;
}

export const buildItemTemplate = item => {
    return {
        id: item.id,
        name: item.name,
        addOns: [],
        modifiers: [],
        price: item.price,
    }
}

export const addToOrder = (oldOrderState, rawItem, quantity) => {
    const { items, subtotal } = oldOrderState;
    const newItem = buildItemTemplate(rawItem);

    let added = false;
    const newItems = [];
    let orderTotal = 0;
    items.map(item => {
        let itemClone = item;
        if(item.id === newItem.id){
            itemClone.quantity = itemClone.quanity + quantity;
            added = true;
        }
        const itemOutput = getSubtotals(itemClone);
        orderTotal = orderTotal + itemOutput.subtotal;
        newItems.push(itemOutput);
    });

    if(!added){
        const itemOutput = getSubtotals(newItem);
        orderTotal = orderTotal + itemOutput.subtotal;
        newItems.push(itemOutput);
    }


    return {
        items: newItems,
        orderTotal
    };

};

export const removeFromOrder = (oldOrderState, itemToRemove, quantity) => {
    const { items, subtotal } = oldOrderState;

    const newItems = [];
    let orderTotal = 0;
    items.map(item => {
        let itemClone = item;
        if(item.id !== itemToRemove.id){
            newItems.push(itemClone);
            orderTotal = orderTotal + itemClone.subtotal;
        }
        else{
            orderTotal = orderTotal - itemClone.subtotal;
        }

    });

    return {
        items: newItems,
        orderTotal
    };

};
