import React from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import * as actions from './actions/actions.js';

class CartContainer extends React.Component{

    printOrder(){
        const { currentOrder } = this.props;

        const itemGroups = Object.keys(currentOrder);
        if(itemGroups.length === 0){
            return (
                <h1>Cart is Empty</h1>
            );
        }
        else{
            return itemGroups.map(itemGroup => (
                <div style={{display: 'flex'}}>
                    <h1>{`x${itemGroup.length}  `}</h1>
                    <h1>{itemGroups[itemGroup[0]].name}</h1>
                    <h1>{itemGroups[itemGroup[0]].price}</h1>
                </div>
            ));
        }
    }

    render(){
        return(
            <div>
                Cart
                {this.printOrder()}
            </div>
        )
    }
}

const mapDispatchToProps = dispatch => bindActionCreators(actions, dispatch)

const mapStateToProps = state => ({
  currentOrder: state.persistentCart.currentOrder,
});

export default connect(mapStateToProps, mapDispatchToProps)(CartContainer)
