import React from 'react';
import ReactDOM from 'react-dom';
import _ from 'lodash'
import uuid from 'uuid/v4';

const NavBadge = (props) =>  {
    // eslint-disable-next-line
    const { section, onSelect, currentPosition, interval } = props;
    console.log(currentPosition, interval, currentPosition >= interval[0]-35 && currentPosition < interval[1]+35)
    const className = currentPosition >= interval[0]-50 && currentPosition < interval[1]-50 ? 'red' : '';
    return (
        <span
            style={{cursor: 'pointer'}}
            onClick={(e) => {onSelect(e)}}
            id={`$scroll-nav-${section}`}
            key={uuid()}
            className={className}
        >
            {section + ',' + interval[0]}
        </span>
    );
}

export default NavBadge;
