import React from 'react'
import LinesEllipsis from 'react-lines-ellipsis'
// import JsxParser from 'react-jsx-parser'

class MenuItem extends React.Component {
  constructor(props){
    this.state ={
      stagedQuantity: 1,
    }
  }

  incrementQuantity(){
    const { stagedQuantity } = this.state;
    this.setState({stagedQuantity: stagedQuantity + 1});
  }

  decrementQuantity(){
    const { stagedQuantity } = this.state;
    this.setState({stagedQuantity: stagedQuantity - 1});
  }

  itemDetails(item) {
    const { lang } = this.props
    let name = item.name
    let desc = item['Item Description Raw']
    let translatedName = 'name-' + lang
    let translatedDesc = 'description-' + lang
    let trimmedName;

    if (lang !== 'en') {
      name = item[translatedName]
      desc = item[translatedDesc]
    }

    if (name){
    trimmedName = name
    if (trimmedName.length > 30) {
      trimmedName = trimmedName.substring(0,31).trim() + '...'
    }}
    else{
      trimmedName = '';
    }

    // <JsxParser
    //   jsx={
    //     `${desc}`
    //   }
    // />

    const clampedDesc = (
      <LinesEllipsis
        text={desc}
        maxLine={3}
        ellipsis='...'
        basedOn='words'
        trimRight
      />
    )

    return (
      <div>
        <h3 className="title">{ trimmedName }</h3>
        <div className="bodyText">{ clampedDesc }</div>
        <div className="info">
          <span className="price">{item.price}</span>
          <span className="tags">{ !!item.tags ? item.tags.join(' ') : null }</span>
        </div>
      </div>
    )
  }

  render() {
    const { item, onClick, addToOrder } = this.props;
    const { stagedQuantity } = this.state;
    let img = item.image ? item.image[0].url : '/mryum_assets/missing_photo.jpg'
    const style = {
      backgroundImage: 'url(' + img + ')',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat'
    }

    // if even/odd //////////////////// GONE
    // if (itemIndex % 2 === 0) {
    return (
      <div className="menuItem" onClick={(e) => onClick(e)}>
        <div className="leftBox itemPhoto" style={style}></div>
        <div className="rightBox">
          { this.itemDetails(item) }
        </div>
        <div style={{display: flex}}>
          <button onClick={(e) => this.decrementQuantity()}>-</button>
          <h1>{stagedQuantity}</h1>
          <button onClick={(e) => this.decrementQuantity()}>+</button>
        </div>
        <button onClick={(e) => addToOrder(item.id)}>Add to Order</button>
      </div>
    )
    // } else {
    //   return (
    //     <div className="menuItem" onClick={(e) => onClick(e)}>
    //       <div className="leftBox">
    //         { this.itemDetails(item) }
    //       </div>
    //       <div className="rightBox itemPhoto" style={style}></div>
    //     </div>
    //   )
    // }
  }
}

export default MenuItem
